package algorithms.string;

public class ValidPalindromeII {

    public static void main(String[] args) {

        String s = "";
        System.out.println(validPalindrome(s));

    }

    public static boolean validPalindrome(String s) {
        String[] split = s.split("");
        for (int i = 0; i < split.length; i++) {
            if (validPalindrome(split,i)) {
                return true;
            }
        }

        return false;
    }


    public static boolean validPalindrome(String[] strings, int indexIgnore) {

        StringBuilder stringBuilder = new StringBuilder();
        for (int i = 0; i < strings.length ; i++) {
            if (i!=indexIgnore) {
                stringBuilder.append(strings[i]);
            }
        }

        String factString = stringBuilder.toString();

        StringBuilder stringBuilder1 = new StringBuilder();
        stringBuilder1.append(factString);

        return stringBuilder1.reverse().toString().equals(factString);
    }
}
